            var translate = function(thumbData){
                var annotationService = Components.classes["@mozilla.org/browser/annotation-service;1"]
                                  .getService(Components.interfaces.nsIAnnotationService);
                var faviconService = Components.classes["@mozilla.org/browser/favicon-service;1"]
                               .getService(Components.interfaces.nsIFaviconService);

                function makeURI(aURL, aOriginCharset, aBaseURI) {
                 var ioService = Components.classes["@mozilla.org/network/io-service;1"]
                                            .getService(Components.interfaces.nsIIOService);
                 return ioService.newURI(aURL, aOriginCharset, aBaseURI);
                }

                var uri = makeURI(thumbData.url);

                var result = {
                    original : thumbData,
                    uri : uri,
                    host : thumbData.host,
                    url : thumbData.url,
                    title : thumbData.title,
                    fav : faviconService.getFaviconImageForPage(uri).spec,
                    originalUrl : thumbData.originalUrl,
                    thumb : annotationService.getAnnotationURI(uri, "jumpstart/thumbs").spec,
                }
                return result;
            }
            
